import { View, Text, Image, TextInput, KeyboardAvoidingView, TouchableWithoutFeedback, Keyboard } from 'react-native'
import React, { useState, useEffect } from 'react'

import { useAuth } from '../../context/AuthContext'
import styles from './styles';
import { LoginButton } from '../../components/LoginButton';
import { TInput} from '../../components/TInput';

import logo from '../../assets/images/logo.png';
import ola1 from '../../assets/images/ola1.png';
import ola2 from '../../assets/images/ola2.png';

const RegisterScreen = ({ navigation }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    phone: '',
    address: '',
    city: '',
    roleId: 1,
  });

  const { signup, isAuthenticated, errors: registerErrors } = useAuth();

  useEffect(() => {
    if (isAuthenticated) {
      navigation.push('Root', { screen: 'Home' });
    }
  }, [isAuthenticated]);

  const handleInputChange = (fieldName, value) => {
    setFormData({
      ...formData,
      [fieldName]: value
    });
  };

  const onSubmit = async () => {
    signup(formData);
  };
  
  return (
    <TouchableWithoutFeedback onPress={() => {Keyboard.dismiss()}}>
      <View style={styles.container}>
        <View style={styles.square}></View>
        <Text style={styles.supportechText}>
          <Text style={styles.blueText}>SOPOR</Text>
          <Text style={styles.tealText}>TECH</Text>
        </Text>
        <Image
          source={logo}
          style={styles.logo}
        />
        <View>
        {registerErrors.map((error, index) => (
          <Text key={index} style={styles.errorText}>{error}</Text>
        ))}
        <TInput
          nameMenu={'person'}
          title={'Nombre'}
          placeholder="Joe Alwyn"
          value={formData.name}
          onChangeText={(text) => handleInputChange('name', text)}
        />
        <TInput
          nameMenu={'email'}
          title={'Email'}
          placeholder="joe_alwyn@gmail.com"
          value={formData.email}
          onChangeText={(text) => handleInputChange('email', text)}
        />
        <TInput
          nameMenu={'lock'}
          title={'Contraseña'}
          placeholder="********"
          secureTextEntry={true}
          value={formData.password}
          onChangeText={(text) => handleInputChange('password', text)}
        />
        <TInput 
          nameMenu={'location-city'}
          title={'Ciudad'}
          placeholder="Lima"
          value={formData.city}
          onChangeText={(text) => handleInputChange('city', text)}
        />
        <TInput
          nameMenu={'location-on'}
          title={'Dirección'}
          placeholder="Av. Javier Prado 123"
          value={formData.address}
          onChangeText={(text) => handleInputChange('address', text)}
        />
        <TInput
          nameMenu={'phone'}
          title={'Teléfono'}
          placeholder="987654321"
          value={formData.phone}
          onChangeText={(text) => handleInputChange('phone', text)}
        />
        <LoginButton onPress={onSubmit} />
      </View>
        <KeyboardAvoidingView enabled>
          <View style={styles.waveContainer}>
            <Image
              source={ola2}
              style={[styles.waveImage, styles.secondWave]}
            />
            <Image
              source={ola1}
              style={[styles.waveImage]}
            />
          </View>
        </KeyboardAvoidingView>
      </View>
    </TouchableWithoutFeedback>
  )
}

export default RegisterScreen
