import React, { useState, useEffect } from 'react';
import { View, Text, ScrollView, TouchableOpacity, Image } from 'react-native';
import { Layout } from "../../../../components/Layout";
import styles from './styles';

import rojo from "../../../../assets/images/rojo.png";
import amarillo from "../../../../assets/images/amarillo.png";
import verde from "../../../../assets/images/verde.png";

const HomeScreen = ({ navigation }) => {
  const [data, setData] = useState([
    { id: 1, nombre: 'Item 1', titulo: 'Título 1', fecha: '01/01/2023', estado: 'verde' },
    { id: 2, nombre: 'Item 2', titulo: 'Título 2', fecha: '02/01/2023', estado: 'verde' },
    { id: 3, nombre: 'Item 3', titulo: 'Título 3', fecha: '03/01/2023', estado: 'verde' },
    { id: 4, nombre: 'Item 3aaaaaaaaaaa', titulo: 'Título 3aaaaaaaaa', fecha: '03/01/2023', estado: 'verde' },
    { id: 5, nombre: 'Item 3aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa', titulo: 'Título 3aaaaaaaaaaaaaaaaaaaaaaaaaaaa', fecha: '03/01/2023', estado: 'verde' },
  ]);

  useEffect(() => {
    // Cambiar el estado a "amarillo" después de 5 segundos
    const interval1 = setTimeout(() => {
      const updatedData = data.map(item => ({ ...item, estado: 'amarillo' }));
      setData(updatedData);
    }, 5000);

    // Cambiar el estado a "rojo" después de 15 segundos
    const interval2 = setTimeout(() => {
      const updatedData = data.map(item => ({ ...item, estado: 'rojo' }));
      setData(updatedData);
    }, 15000);

    return () => {
      clearTimeout(interval1);
      clearTimeout(interval2);
    };
  }, []); // El efecto solo se ejecuta una vez al montar el componente

  return (
    <Layout 
      navigation={navigation}
      title="Inicio"
      screen={
        <ScrollView contentContainerStyle={styles.container}>
          <ScrollView horizontal>
            <View style={styles.tableContainer}>
              <View style={styles.table}>
                <View style={styles.tableHeader}>
                  <Text style={[styles.headerCell, { width: 100 }]}>Nombre</Text>
                  <Text style={[styles.headerCell, { width: 200 }]}>Título</Text>
                  <Text style={[styles.headerCell, { width: 100 }]}>Fecha</Text>
                  <Text style={[styles.headerCell, { width: 150 }]}>Estado</Text>
                </View>
                {data.map(item => (
                  <TouchableOpacity
                    style={styles.tableRow}
                    key={item.id}
                  >
                    <Text style={[styles.tableCell, { width: 100 }]}>{item.nombre}</Text>
                    <Text style={[styles.tableCell, { width: 200 }]}>{item.titulo}</Text>
                    <Text style={[styles.tableCell, { width: 100 }]}>{item.fecha}</Text>
                    <View style={[{ width: 150 }, styles.statusCell]}>
                      <View style={styles.statusImageContainer}>
                        {item.estado === 'rojo' && <Image source={rojo} style={styles.statusImage} />}
                        {item.estado === 'amarillo' && <Image source={amarillo} style={styles.statusImage} />}
                        {item.estado === 'verde' && <Image source={verde} style={styles.statusImage} />}
                      </View>
                    </View>
                  </TouchableOpacity>
                ))}
              </View>
            </View>
          </ScrollView>
        </ScrollView>
      }
    />
  );
};

export default HomeScreen;
