import React, { useEffect, useState } from 'react';
import { View, Button } from 'react-native';

import HomeComp from '../components/HomeComp';
import Header from '../components/Header';
import Footer from '../components/Footer'

import { getUsers } from '../Api';
import colors from '../config/colors'

const HomeScreen = ({navigation}) => {
  const [menuVisible, setMenuVisible] = useState(false);
  const [user, setUser] = useState(null);

  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  }

  const loadUser = async () => {
    const data = await getUsers();
    const fetchedUser = data[0]['name'];

    setUser(fetchedUser);
  }

  useEffect(() => {
    loadUser();
  }, [])

  return (
    <View style={styles.container}>
      <Header nombreVista={user} onMenuPress={toggleMenu} />
      {menuVisible && (
        <View style={styles.overlay}>
          <View style={styles.menu}>
            <Button title="Nueva Consulta" onPress={() => navigation.navigate('New')} />
            <Button title="Consultas Pendientes" onPress={() => navigation.navigate('Pending')} />
            <Button title="Consultas Cerradas" onPress={() => navigation.navigate('Closed')} />
            <Button title="Cerrar Sesion" onPress={() => navigation.navigate('Login')} />
          </View>
        </View>
      )}
      <HomeComp />
      <Footer />
    </View>
  );
}

const styles = {
  container: {
    flex: 1,
  },
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1,
  },
  menu: {
    backgroundColor: colors.primary,
    position: 'absolute',
    top: 110,
    right: 0,
    padding: 10,
    zIndex: 100,
  },
  menuText: {
    color: 'white',
  },
};

export default HomeScreen;
